                  className="flex-1 h-11"
                  data-testid="button-toggle-bold"
                >
                  <Bold className="w-4 h-4" />
                </Button>
                <Button
                  variant={isItalic ? "default" : "outline"}
                  size="icon"
                  onClick={() => setIsItalic(!isItalic)}
                  className="flex-1 h-11"
                  data-testid="button-toggle-italic"
                >
